// Faz os imports que serão usados
import React, { Component } from 'react';
import { getCategories } from '../services/api';

// Cria uma classe que cuida das página dos Favoritos
class Favorites extends Component {
  constructor() {
    super();
    this.state = {
      produtosLista: [],
    };
  }

  // No DidMount chama a função que faz a requisição das favoritas na API
  async componentDidMount() {
    const resposta = await getCategories();
    console.log('resposta', resposta);
    this.setState({
      produtosLista: resposta,
    });
  }

  render() {
    const { produtosLista } = this.state;
    return (
      <div data-testid="category" />
    );
  }
}

export default Favorites;
